mapper.select:
SELECT DISTINCT res.id AS id, res.NAME, res.parent_id, res.configure, res.type, res.sort, res.creator_id, res.creator, res.reviser_id, res.reviser, res.create_time, res.update_time FROM t_resource res left JOIN t_role_resource rr ON rr.resource_id = res.id left JOIN t_user_role ur ON ur.role_id = rr.role_id where 1=1
AND res.TYPE =1
and ur.user_id=#{userId,jdbcType=VARCHAR}
