mapper.select:
select t1.id,t1.name,t2.name as folder_name,t1.view_range,t1.create_time, t3.suffix_type, ( SELECT GROUP_CONCAT(NAME) FROM t_document_label WHERE id IN ( SELECT label_id FROM t_document_label_union WHERE document_id = t1.id ) ) AS label from t_document t1 left join t_document_folder t2 on t1.folder_id=t2.id left join t_attachment t3 on t1.attachment_id=t3.id where 1=1
AND t1.id IN ( SELECT document_id from t_document_label_union WHERE label_id IN (#{labelId,jdbcType=VARCHAR}) )
and t1.org_id=#{orgId,jdbcType=VARCHAR}
and t1.name like #{name,jdbcType=VARCHAR}
and t1.creator like #{creator,jdbcType=VARCHAR}
and t1.create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND CURRENT_TIME
and t1.create_time BETWEEN CURRENT_TIMEAND AND #{endTime,jdbcType=VARCHAR}
and t1.create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}
and t1.view_range=1 and t1.creator_id=#{userId,jdbcType=VARCHAR}
union
select t1.id,t1.name,t2.name as folder_name,t1.view_range,t1.create_time, t3.suffix_type, ( SELECT GROUP_CONCAT(NAME) FROM t_document_label WHERE id IN ( SELECT label_id FROM t_document_label_union WHERE document_id = t1.id ) ) AS label from t_document t1 left join t_document_folder t2 on t1.folder_id=t2.id left join t_attachment t3 on t1.attachment_id=t3.id where (t1.view_range=2 or t1.view_range=3)
AND t1.id IN ( SELECT document_id from t_document_label_union WHERE label_id IN (#{labelId,jdbcType=VARCHAR}) )
and (t1.org_id in (select id from t_org where parent_id=#{userOrgId,jdbcType=VARCHAR}) or t1.org_id=#{userOrgId,jdbcType=VARCHAR})
and (t1.org_id in (select id from t_org where parent_id=#{orgId,jdbcType=VARCHAR}) or t1.org_id=#{orgId,jdbcType=VARCHAR})
and t1.name like #{name,jdbcType=VARCHAR}
and t1.creator like #{creator,jdbcType=VARCHAR}
and t1.create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND CURRENT_TIME
and t1.create_time BETWEEN CURRENT_TIMEAND AND #{endTime,jdbcType=VARCHAR}
and t1.create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}

select t1.id,t1.name,t2.name as folder_name,t1.view_range,t1.create_time,	t3.suffix_type,
( SELECT GROUP_CONCAT(NAME) FROM t_document_label WHERE id IN ( SELECT label_id FROM t_document_label_union
WHERE document_id = t1.id ) ) AS label
from t_document t1
left join t_document_folder t2 on t1.folder_id=t2.id
left join t_attachment t3 on t1.attachment_id=t3.id
where 1=1

    AND t1.id IN ( SELECT document_id from t_document_label_union WHERE label_id IN
    (#{labelId,jdbcType=VARCHAR}) )


    and t1.org_id=#{orgId,jdbcType=VARCHAR}


    and t1.name like #{name,jdbcType=VARCHAR}


    and t1.creator like #{creator,jdbcType=VARCHAR}


    and t1.folder_id=#{folderId,jdbcType=VARCHAR}


    and t1.create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND CURRENT_TIME


    and t1.create_time BETWEEN CURRENT_TIMEAND AND #{endTime,jdbcType=VARCHAR}


    and t1.create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}

and t1.view_range=1 and t1.creator_id=#{userId,jdbcType=VARCHAR}

union

select t1.id,t1.name,t2.name as folder_name,t1.view_range,t1.create_time,	t3.suffix_type,
( SELECT GROUP_CONCAT(NAME) FROM t_document_label WHERE id IN ( SELECT label_id FROM t_document_label_union
WHERE document_id = t1.id ) ) AS label
from t_document
t1
left join t_document_folder t2 on t1.folder_id=t2.id
left join t_attachment t3 on t1.attachment_id=t3.id
where
(t1.view_range=2 or t1.view_range=3)

    AND t1.id IN ( SELECT document_id from t_document_label_union WHERE label_id IN
    (#{labelId,jdbcType=VARCHAR}) )


    and (t1.org_id in (select id from t_org where parent_id=#{userOrgId,jdbcType=VARCHAR}) or
    t1.org_id=#{userOrgId,jdbcType=VARCHAR})


    and t1.folder_id=#{folderId,jdbcType=VARCHAR}


    and t1.name like #{name,jdbcType=VARCHAR}


    and t1.creator like #{creator,jdbcType=VARCHAR}


    and t1.create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND CURRENT_TIME


    and t1.create_time BETWEEN CURRENT_TIMEAND AND #{endTime,jdbcType=VARCHAR}


